function a($nivel,$texto) {
	global $n1,$n2,$n3,$tn1,$tn2,$tn3;
	if ($nivel===1) {
		++$n1;
		$tn1[$n1] = $texto;
	}
	if ($nivel===2) {
		++$n2;
		$tn2[$n1][$n2] = $texto;
	}
	if ($nivel===3) {
		++$n3;
		$tn3[$n2][$n3] = $texto;
	}
}